.score {
  margin: 0;
  color: rgb(12, 110, 155);
}
.death {
  margin: 0;
  color: rgb(209, 44, 113);
}

.arrows {
  border: 2px solid black;
  color: cornflowerblue;
  background-color: #ccc;
  font-size: 2em;
  height: 100%;

}
.arrows:hover {
  color: rgb(200, 149, 100);
}
.arrows:active {
  color: orange;
}
#arrowBlock {
  /* background: red; */
  display: grid;
  width: 50%;
  grid-template: 1fr 1fr 1fr/ 1fr 1fr 1fr;
/*   grid-template-rows: 5em 5em;
  grid-column: auto 2fr auto; */
}
#up {
  /* font-size: 2em; */
  height: 100%;
  grid-column: 2 / 3;
  grid-row: 1 / 2;
  /* font-size: 3em; */
}
#left {
  height: 100%;
  grid-column: 1 / 2;
  grid-row: 2 / 3;
}
#down {
  height: 100%;
  grid-column: 2 / 3;
  grid-row: 2 / 3;
}
#right {
  height: 100%;
  grid-column: 3 / 4;
  grid-row: 2 / 3;
}
canvas {
  background-color: #000;
}

#isPhone {
  color: black;
}

@media only screen and (max-width: 600px) {
  #isPhone {
    color: white;
  }
  body {
    background-color: #bbb;
  }
  #center {
    border: 0px;
  }
  canvas {
    width: 100%;
  }
}
